    override func tableView(_ tableView: UITableView, cellForRowAt indexPath: IndexPath) -> UITableViewCell {
        let cell = tableView.dequeueReusableCell(withIdentifier: "dataNegara", for: indexPath)
        
        
        cell.textLabel?.text = namaNegara[indexPath.row]
       

        return cell
    }
   
    
    override func tableView(_ tableView: UITableView, didSelectRowAt indexPath: IndexPath) {
        showAlert(title: namaNegara[indexPath.row], message: "Anda memilih \(namaNegara[indexPath.row])")
    
        print(namaNegara[indexPath.row])
    }
    
    
    func showAlert(title: String, message:String)  {
        let alert       = UIAlertController(title: title, message: message, preferredStyle: .alert)
        let btnClose    = UIAlertAction(title: "Close", style: .cancel, handler: nil)
        
        alert.addAction(btnClose)
        
        present(alert, animated: true, completion: nil)
        
    }
    
